exports.html = function(config,_mastCssFiles) {
	var html = '<!-- Import template styles -->\n';

	if(config.appEnvironment=='production') {
		html+='<link rel="stylesheet" type="text/css" media="all" href="/_target/app.css"/>';
	}
	else {
		_.each(_mastCssFiles,function(path) {
			html+='<link rel="stylesheet" type="text/css" media="all" href="'+path+'"/>';
		});
	}
	
	return html;
};