exports.html = function(config,_mastJsFiles) {
	var html = '';
	
	if (config.mastSocket) {
		html += '<!-- TODO: copy socket.io into the lib/client dir automatically on app start and bundle it as part of the compilation process -->\n';
		html += '<script type="text/javascript" src="/socket.io/socket.io.js"></script>';
	}

	if(config.appEnvironment=='production') {
		html+='<script type="text/javascript" src="/_target/app.js"></script>';
	}
	else {
		_.each(_mastJsFiles,function(path) {
			html+='<script type="text/javascript" src="'+path+'"></script>';
		});
	}
	
	return html;
};