// Determine whether Rigging library is in place
// to detect whether integrated Mast support should be enabled
exports.layout = function(config,res) {
	res.local('rigging',{
		css: require('./mastCss').html(config,_mastCssFiles),
		js: require('./mastJs').html(config,_mastJsFiles),
		templateLibrary: require('./templateLibrary').html(config)
	});
}