exports.html = function(config,filename) {
	var html = '<div style="display:none;" id="mast-template-library">\n';

	function isEjs(filename) { return filename.match(/.+\.ejs$/g); }

	// Get all files in components dir
	var viewRelativePath = config.appPath+"/mast/templates", 
	appRelativePath = 'mast/templates';
	fs.readdirSync(config.appPath+"/"+appRelativePath).forEach(function(filename) {
		if (filename[0] === '_' || !isEjs(filename)) { return; }
		var filepath = path.join(viewRelativePath, filename);
		html += fs.readFileSync(filepath,'utf8') + "\n";
	});
	
	html+="</div>";
	return html;
};

